mapper.insert:
INSERT INTO takku_coupon
(
funding_id, user_id, store_id, coupon_code,
use_status, reviewed,
created_at, expired_at
) VALUES (
#{fundingId}, #{userId}, #{storeId},
#{couponCode},
#{useStatus}, #{reviewed}, #{createdAt}, #{expiredAt}
)

mapper.update:
UPDATE
takku_coupon
SET use_status = #{useStatus},
used_at = #{usedAt}
WHERE
coupon_code = #{couponCode}
