mapper.update:
UPDATE takku_review
SET
	rating = #{rating},
	content = #{content}
WHERE review_id = #{reviewId}

mapper.select:
SELECT r.*, u.nickname
FROM takku_review r
JOIN takku_user u ON r.user_id = u.user_id
WHERE r.product_id = #{productId}
ORDER BY r.created_at DESC
LIMIT #{pageSize} OFFSET #{offset}
