mapper.select:
SELECT *
FROM takku_user
WHERE phone = #{phone}
  AND password = #{password}
  AND user_type = #{userType}

mapper.update:
UPDATE takku_user
SET password = #{password},
    nickname = #{nickname},
    is_partner = #{isPartner}
WHERE user_id = #{userId}

mapper.select:
SELECT COUNT(*) AS cnt
FROM takku_user
WHERE phone = #{phone}
  AND user_type = #{userType}

mapper.update:
UPDATE takku_user
SET point = point - #{usePoint}
WHERE user_id = #{userId}
  AND point >= #{usePoint}

UPDATE takku_user
SET point = point + #{usePoint}
WHERE user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM takku_user
WHERE phone = #{phone}
  AND user_type = #{userType}

SELECT *
FROM takku_user
WHERE user_type = #{userType}
  AND phone = #{phone}
  AND name = #{name}
